import React from "react";
import './instruments.css';
import { Modal } from 'react-responsive-modal';
import {useState } from 'react';



function Instruments(props) {

    const [open, setOpen] = useState(false);
    const [selected, setSelected] = useState(-1);


    const onOpenModal = () =>{
        setOpen(true);
    } ;
    const onCloseModal = () => setOpen(false);

    return (
        <>
            {props.products?.map((article) => {
                return (

                    <div className="App" key={article.instrumento} onClick={onOpenModal}>
                        <div id="instTable" key={article.instrumento} onClick={setSelected(article.id)}>
                            <div id="artSquare"
                                className="artTabs"
                                type="button"
                                key={article.instrumento}
                                name={article.instrumento}
                                style={{
                                    width: '305px',
                                    height: '490px',
                                    margin: '2px solid black',
                                }}
                            >
                                <img
                                    className="imagesDisplay"
                                    key={article.instrumento}
                                    name={article.instrumento}
                                    alt={article.instrumento}
                                    src={article.url}
                                    style={{
                                        width: '250px',
                                        height: '280px',
                                        marginBottom: '10px',

                                    }}
                                />

                                <p id="artName">{article.instrumento}</p>
                                <p id="artGen">{article.genero}</p>
                                <p id="artP">{"$" + article.precio}</p>
                            </div>
                        </div>



                    </div>

                );
            })}
            <Modal open={open} onClose={onCloseModal} center>
                            <img
                                className="imagesDisplay"
                                key={props.instrumento}
                                name={props.instrumento}
                                alt={props.instrumento}
                                src={props.products[selected].url}
                                style={{
                                    width: '250px',
                                    height: '280px',
                                    marginBottom: '10px',

                                }}
                            />
                        </Modal>
        </>
    );
}

export default Instruments;
